import React, {
  useState
} from 'react';
import {
  EmptyState,
  Layout,
  Page
} from '@shopify/polaris';
import {
  ResourcePicker
} from '@shopify/app-bridge-react';
import store from 'store-js';

function Index() {
  const [modal, setModal] = useState({
    open: false
  });
  const EmptyState = !store.get('ids');

  function handleSelection(resources) {
    const idsFromResources = resources.selection.map((product) => product.id);
    setModal({
      open: false
    });
    store.set('ids', idsFromResources);
    console.log('this is product ids', store.get('ids'));
  }

  return ( <
    Page >
    <
    ResourcePicker resourceType = 'Product'
    showVariants = {
      false
    }
    open = {
      modal.open
    }
    onCancel = {
      () => setModal({
        open: false
      })
    }
    onSelection = {
      (resources) => handleSelection(resources)
    }
    /> <
    Layout >
    <
    EmptyState heading = 'Manage your invetory transfers'
    action = {
      {
        content: 'Add transfer',
        onAction: () => setModal({
          open: true
        }),
      }
    }
    secondaryAction = {
      {
        content: 'Learn more',
        url: 'https://help.shopify.com',
      }
    }
    image = 'https://cdn.shopify.com/s/files/1/0757/9955/files/empty-state.svg' >
    <
    p > Select Products < /p> <
    /EmptyState> <
    /Layout> <
    /Page>
  );
}

export default Index;
